mapper.select:
SELECT
       th.theater_pk,
       th.the_name
FROM screentime_tb tim, theater_tb th
WHERE tim.movie_pk= #{movie_pk}
  AND tim.scrt_date = #{date}
  AND tim.theater_pk = th.theater_pk
GROUP BY th.theater_pk;

SELECT tim.*,
       (sum(book.book_adult_cnt) + sum(book.book_youth_cnt)) AS booked,
       group_concat(book.book_seat_num)                      AS seat
FROM screentime_tb tim,
     booking_tb book
WHERE tim.movie_pk = #{movie_pk}
  AND tim.scrt_date = #{date}
  AND tim.theater_pk = #{theater_pk}
  AND tim.screen_pk = #{screen_pk}
  AND book.scrtime_pk = tim.scrtime_pk
GROUP BY tim.scrtime_pk;

SELECT tim.screen_pk, tim.movie_pk, tim.scrt_date, tim.scrt_stime, scr.scr_name, scr.seat_plot, scr.scr_tot_seat, th.the_name FROM screentime_tb tim, screen_tb scr, theater_tb th, booking_tb book WHERE scr.screen_pk = tim.screen_pk AND scr.theater_pk = th.theater_pk
AND tim.movie_pk = #{movie_pk}
AND tim.scrt_date = #{date}
AND theater_pk = #{theater_pk}

SELECT
   scr.screen_pk ,scr.scr_name, mt.m_name, mt.m_age_grd, scr.scr_tot_seat
FROM screentime_tb scrt, movie_tb mt, screen_tb scr
WHERE scrt.movie_pk = #{movie_pk}
  AND scrt.scrt_date = #{scrt_date}
  AND scrt.theater_pk = #{theater_pk}
  AND mt.movie_pk = scrt.movie_pk
  AND scr.screen_pk = scrt.screen_pk;
